#pragma once
#include <base/Platform.hpp>

namespace base {

#ifndef TERMINAL_DISABLE_COLORS
#if defined(PLATFORM_MOBILE) && defined(PLATFORM_APPLE)
#define TERMINAL_DISABLE_COLORS
#endif
#endif

#if TERMINAL_DISABLE_COLORS
#define TERMINAL_ESCAPE(x)
#else
#define TERMINAL_ESCAPE(x) x
#endif

#define TERMINAL_RESET_SEQUENCE TERMINAL_ESCAPE("\x1b[0m")

#define TERMINAL_COLOR_SEQUENCE_RED TERMINAL_ESCAPE("\x1b[31;1m")
#define TERMINAL_COLOR_SEQUENCE_GREEN TERMINAL_ESCAPE("\x1b[32;1m")
#define TERMINAL_COLOR_SEQUENCE_YELLOW TERMINAL_ESCAPE("\x1b[33;1m")
#define TERMINAL_COLOR_SEQUENCE_BLUE TERMINAL_ESCAPE("\x1b[34;1m")
#define TERMINAL_COLOR_SEQUENCE_MAGENTA TERMINAL_ESCAPE("\x1b[35;1m")
#define TERMINAL_COLOR_SEQUENCE_CYAN TERMINAL_ESCAPE("\x1b[36;1m")
#define TERMINAL_COLOR_SEQUENCE_WHITE TERMINAL_ESCAPE("\x1b[37;1m")

#define TERMINAL_COLOR_RED(x) TERMINAL_COLOR_SEQUENCE_RED x TERMINAL_RESET_SEQUENCE
#define TERMINAL_COLOR_GREEN(x) TERMINAL_COLOR_SEQUENCE_GREEN x TERMINAL_RESET_SEQUENCE
#define TERMINAL_COLOR_YELLOW(x) TERMINAL_COLOR_SEQUENCE_YELLOW x TERMINAL_RESET_SEQUENCE
#define TERMINAL_COLOR_BLUE(x) TERMINAL_COLOR_SEQUENCE_BLUE x TERMINAL_RESET_SEQUENCE
#define TERMINAL_COLOR_MAGENTA(x) TERMINAL_COLOR_SEQUENCE_MAGENTA x TERMINAL_RESET_SEQUENCE
#define TERMINAL_COLOR_CYAN(x) TERMINAL_COLOR_SEQUENCE_CYAN x TERMINAL_RESET_SEQUENCE
#define TERMINAL_COLOR_WHITE(x) TERMINAL_COLOR_SEQUENCE_WHITE x TERMINAL_RESET_SEQUENCE

}  // namespace base